import React, { useState } from "react";
import axios from 'axios';
import { Link, Navigate, useLocation } from "react-router-dom";

import Header from "../Include/Header";
import Carousel from "react-bootstrap/Carousel";

function AddProperty() {
    const [title, setTitle] = React.useState('');
    const [image, setImage] = React.useState('');
    const [price, setPrice] = React.useState('');
    const [contact, setContact] = React.useState('');
    const [rating, setRating] = React.useState('');
    const [reviews, setReviews] = React.useState('');
    const [location, setLocation] = React.useState('');
    const [link, setLink] = React.useState('');
    const [selectedFile, setSelectedFile] = React.useState(null);
    const [msg, setMsg] = React.useState('');

    
    const add_property = () =>{
        const formData = {title: title, price: price, contact: contact, rating: rating, reviews: reviews, location: location, link:link, selectedFile:selectedFile };
        // const formData = new FormData();
        formData.append("selectedFile", selectedFile);
        let result = axios.post("http://localhost:8000/api/add-propety", formData, {
            headers: {
                "Content-Type": "multipart/form-data",
            }
        }).then((response) => {
            if (response.data.status === 200) {
                localStorage.setItem("property", JSON.stringify(response.data.data));
                setMsg(response.data.message)
                console.log(response.data)
                alert("ok")
                // return <Navigate to="/all-properties" />;
            }

            if (response.data.status === "failed" && response.data.success === false) 
            {
                setMsg(response.data.message)
                setTimeout(() => {
                    setMsg('')
                }, 2000);
            }
        })
        .catch((error) => {
            console.log(error);
        });
    }

    // const handleSubmit = async(event) => {
    //     event.preventDefault()
    //     const formData = new FormData();
    //     formData.append("selectedFile", selectedFile);
    //     try {
    //       const response = await axios({
    //         method: "post",
    //         url: "http://localhost:8000/api/add-propety",
    //         data: formData,
    //         headers: { "Content-Type": "multipart/form-data" },
    //       });
    //     } catch(error) {
    //       console.log(error)
    //     }
    // }

    const handleFileSelect = (event) => {
        setSelectedFile(event.target.files[0])
    }

  return (
    <>
      <main className="main-sectionWrapper">
        <div id="main-section" className="primary-scroll">
          <Header />
          <div className="page-header">
            <div className="page-header__title">
              <Link to="/welcome">
                <i className="bx bx-chevron-left"></i>
              </Link>
              Property
            </div>
          </div>

          <div className="property">
            <div className="container">
              <div className="auth__title">Add Property</div>
               <form action="#" className="auth-form" > 

                <div className="auth-form__fields">
                  <label>
                    Title
                  </label>
                  <input
                    type="text"
                    name="title"
                    placeholder="Please Enter Title"
                    onChange={(e) => setTitle(e.target.value)}
                  />
                </div>

                <div className="auth-form__fields">
                  <label>Price</label>
                  <div>
                    <input
                      type="number"
                      name="price"
                      placeholder="Please Enter Price"
                      onChange={(e) => setPrice(e.target.value)}
                    />
                  </div>
                </div>

                <div className="auth-form__fields">
                  <label>Contact</label>
                  <div>
                    <input
                      type="text"
                      name="contact"
                      placeholder="Please Enter Contact"
                      onChange={(e) => setContact(e.target.value)}
                    />
                  </div>
                </div>

                <div className="auth-form__fields">
                  <label>Rating</label>
                  <div>
                    <input
                      type="number"
                      name="rating"
                      max={5}
                      placeholder="Please Enter Rating"
                      onChange={(e) => setRating(e.target.value)}
                    />
                  </div>
                </div>
                <div className="auth-form__fields">
                  <label>Reviews</label>
                  <div>
                    <input
                      type="text"
                      name="reviews"
                      placeholder="Please Enter Reviews"
                      onChange={(e) => setReviews(e.target.value)}
                    />
                  </div>
                </div>
                <div className="auth-form__fields">
                  <label>Location</label>
                  <div>
                    <input
                      type="text"
                      name="location"
                      placeholder="Please Enter Location"
                      onChange={(e) => setLocation(e.target.value)}
                    />
                  </div>
                </div>
                <div className="auth-form__fields">
                  <label>location Link</label>
                  <div>
                    <input
                      type="text"
                      name="link"
                      placeholder="Please Enter Contact"
                      onChange={(e) => setLink(e.target.value)}
                    />
                  </div>
                </div>
                <div className="property-upload">
                    <label for="gallery" className="themeBtn">
                        <i className='bx bxs-image'></i>Images
                        <input type="file" id="gallery" onChange={handleFileSelect}/>
                    </label>
                </div>

                <div className="auth-form__fields">
                  <span className="text-danger">{msg}</span>
                  {/* <button type="submit" className="themeBtn themeBtn--full"> 
                    Add property
                  </button> */}
                  <a onClick={add_property} className="themeBtn themeBtn--full"> 
                    Add property
                  </a>
                  {/* <Link to="/acount-Varify" className="themeBtn themeBtn--full">Sign In</Link> */}
                </div>
              </form>
            </div>
          </div>
        </div>
      </main>
    </>
  );
}
export default AddProperty;
